import React, { Component } from "react";
// import GoogleMapReact from 'google-map-react';
// import Marker from 'google-maps-react';

import { Map, InfoWindow, Marker, GoogleApiWrapper } from "google-maps-react";

export class MapContainer extends Component {


  state = {
    showingInfoWindow: false,
    activeMarker: {},
    selectedPlace: {},
    choice:"all"
  };
 
  onMarkerClick = (props, marker, e) =>
    this.setState({
      selectedPlace: props,
      activeMarker: marker,
      showingInfoWindow: true
    });
 
  onMapClicked = (props) => {
    if (this.state.showingInfoWindow) {
      this.setState({
        showingInfoWindow: false,
        activeMarker: null
      })
    }
  };


  render() {

    return (
      <Map google={this.props.google}
      style={{ height: 600, width: 700}}
      initialCenter={{
        lat: 30.2672,
        lng: -97.7431
      }}
      zoom={14}
      onClick={this.onMapClicked}>
        {this.props.markers[this.state.choice].map(marker => (
          <Marker
            title={marker.title}
            name={marker.name}
            position={marker.position}
            url={marker.url}
            onClick={this.onMapClicked}
            onClick={this.onMarkerClick}
          />
        ))}
          <InfoWindow
          marker={this.state.activeMarker}
          visible={this.state.showingInfoWindow}>
            <div>
              <h4>{this.state.selectedPlace.name}</h4>
              <a href={this.state.selectedPlace.picture}>{this.state.selectedPlace.picture}</a>
              <a href={this.state.selectedPlace.url}>{this.state.selectedPlace.url}</a>
            </div>
        </InfoWindow>
      </Map>
    );
  }
}

export default GoogleApiWrapper({
  apiKey: "AIzaSyAuyheENMul3n_eUNnx_bKEV7BFrpMaSGE"
})(MapContainer);





